import 'package:cloud_firestore/cloud_firestore.dart';class CategoryModel {  final int totalTodo;  final int done;  final DocumentReference reference;  final String title;  CategoryModel({    this.title,    this.totalTodo,    this.done,    this.reference  });  factory CategoryModel.fromFirestore(DocumentSnapshot doc) {    Map data = doc.data;    assert(doc.documentID != null);    assert(data['Total'] != null);    assert(data['Done'] != null);    assert(doc.reference != null);    assert(data['Title'] != null);    return CategoryModel(        totalTodo: data['Total'],        done: data['Done'],        title: doc.documentID,        reference: doc.reference    );  }  @override  String toString() => "TODO<$title:$totalTodo:$done>";  factory CategoryModel.toInsert(String title) {    return CategoryModel(      title: title,      done: 0,      totalTodo: 0    );  }}